                            <input
                              type="checkbox"
                              checked={calendarSchedule[formatDateKey(selectedDate)]?.timeSlots?.[timeSlot] || false}
                              onChange={() => toggleCalendarScheduleSlot(selectedDate, timeSlot)}
                            />
