import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import axios from 'axios';

const BACKEND_URL = process.env.REACT_APP_BACKEND_URL;
const API = `${BACKEND_URL}/api`;

// Timer presets in minutes and seconds for easy testing
const TIMER_PRESETS = [
  { label: "10s", value: 10/60, unit: "seconds" },
  { label: "30s", value: 30/60, unit: "seconds" },
  { label: "1m", value: 1, unit: "minutes" },
  { label: "5m", value: 5, unit: "minutes" },
  { label: "10m", value: 10, unit: "minutes" },
  { label: "15m", value: 15, unit: "minutes" },
  { label: "30m", value: 30, unit: "minutes" },
  { label: "45m", value: 45, unit: "minutes" },
  { label: "60m", value: 60, unit: "minutes" }
];

// Days of the week
const DAYS = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday'];

// Time slots from 7:00 to 17:00 (30-min intervals)
const generateTimeSlots = () => {
  const slots = [];
  for (let hour = 7; hour <= 17; hour++) {
    slots.push(`${hour.toString().padStart(2, '0')}:00`);
    if (hour < 17) {
      slots.push(`${hour.toString().padStart(2, '0')}:30`);
    }
  }
  return slots;
};

const TIME_SLOTS = generateTimeSlots();

const SpotifyTimer = () => {
  // Authentication state
  const [accessToken, setAccessToken] = useState(null);
  const [refreshToken, setRefreshToken] = useState(null);
  const [user, setUser] = useState(null);

  // Track selection state
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [selectedTracks, setSelectedTracks] = useState([]);
  const [isSearching, setIsSearching] = useState(false);

  // Timer state
  const [timerDuration, setTimerDuration] = useState(30); // minutes
  const [playDuration, setPlayDuration] = useState(30); // seconds
  const [timeRemaining, setTimeRemaining] = useState(30 * 60); // seconds
  const [isTimerRunning, setIsTimerRunning] = useState(false);
  const [isPlaying, setIsPlaying] = useState(false); // New state to track if song is playing
  const [currentTrackIndex, setCurrentTrackIndex] = useState(0);
  const [trackPositions, setTrackPositions] = useState({}); // Store position for each track

  // New enhanced features
  const [activeTab, setActiveTab] = useState('timer'); // timer, schedule, settings
  const [weeklySchedule, setWeeklySchedule] = useState({});
  const [playbackTimingMode, setPlaybackTimingMode] = useState('start'); // 'start' or 'end'
  const [absoluteTimeMode, setAbsoluteTimeMode] = useState(false);
  const [absoluteTimeSlots, setAbsoluteTimeSlots] = useState({
    hourly: false,
    halfHourly: false,
    custom: {}
  });
  const [notificationsEnabled, setNotificationsEnabled] = useState(false);

  // UI state
  const [showSettings, setShowSettings] = useState(false);
  const [currentlyPlaying, setCurrentlyPlaying] = useState(null);
  const [devices, setDevices] = useState([]);
  const [customTimerInput, setCustomTimerInput] = useState('');

  // Refs for intervals
  const timerIntervalRef = useRef(null);
  const playbackIntervalRef = useRef(null);
  const absoluteTimerRef = useRef(null);

  // Initialize app
  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const code = urlParams.get('code');

    if (code) {
      handleAuthCallback(code);
    } else {
      // Try to load stored tokens
      const storedAccessToken = localStorage.getItem('spotify_access_token');
      const storedRefreshToken = localStorage.getItem('spotify_refresh_token');

      if (storedAccessToken) {
        setAccessToken(storedAccessToken);
        setRefreshToken(storedRefreshToken);
      }
    }

    // Request notification permission
    if ('Notification' in window) {
      Notification.requestPermission().then(permission => {
        setNotificationsEnabled(permission === 'granted');
      });
    }

    // Initialize weekly schedule
    initializeWeeklySchedule();
  }, []);

  // Load user profile when access token is available
  useEffect(() => {
    if (accessToken) {
      loadUserProfile();
      loadDevices();
      loadTimerSettings();
      setupAbsoluteTimeMonitoring();
    }
  }, [accessToken]);

  // Timer effect
  useEffect(() => {
    if (isTimerRunning && !isPlaying && timeRemaining > 0) {
      // Only count down when not playing a song
      timerIntervalRef.current = setInterval(() => {
        setTimeRemaining(prev => {
          if (prev <= 1) {
            // Timer reached zero - play track but don't reset timer yet
            playCurrentTrack();
            return 0; // Keep at zero while playing
          }
          return prev - 1;
        });
      }, 1000);
    } else {
      if (timerIntervalRef.current) {
        clearInterval(timerIntervalRef.current);
      }
    }

    return () => {
      if (timerIntervalRef.current) {
        clearInterval(timerIntervalRef.current);
      }
    };
  }, [isTimerRunning, isPlaying, timeRemaining, timerDuration]);

  // Persist timer state
  useEffect(() => {
    if (user?.id) {
      localStorage.setItem(`timer_state_${user.id}`, JSON.stringify({
        timeRemaining,
        isTimerRunning,
        currentTrackIndex,
        trackPositions,
        weeklySchedule,
        playbackTimingMode,
        absoluteTimeMode,
        absoluteTimeSlots
      }));
    }
  }, [timeRemaining, isTimerRunning, currentTrackIndex, trackPositions, user?.id, weeklySchedule, playbackTimingMode, absoluteTimeMode, absoluteTimeSlots]);

  const initializeWeeklySchedule = () => {
    const schedule = {};
    DAYS.forEach(day => {
      schedule[day] = {
        wholeDay: false,
        timeSlots: {}
      };
      TIME_SLOTS.forEach(slot => {
        schedule[day].timeSlots[slot] = false;
      });
    });
    setWeeklySchedule(schedule);
  };

  const setupAbsoluteTimeMonitoring = () => {
    if (absoluteTimeMode && (absoluteTimeSlots.hourly || absoluteTimeSlots.halfHourly || Object.keys(absoluteTimeSlots.custom).length > 0)) {
      absoluteTimerRef.current = setInterval(() => {
        const now = new Date();
        const currentTime = `${now.getHours().toString().padStart(2, '0')}:${now.getMinutes().toString().padStart(2, '0')}`;
        const currentDay = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'][now.getDay()];

        // Check if we should play based on absolute time settings
        let shouldPlay = false;

        if (absoluteTimeSlots.hourly && now.getMinutes() === 0) {
          shouldPlay = true;
        }

        if (absoluteTimeSlots.halfHourly && now.getMinutes() === 30) {
          shouldPlay = true;
        }

        if (absoluteTimeSlots.custom[currentTime]) {
          shouldPlay = true;
        }

        // Check weekly schedule
        if (shouldPlay && weeklySchedule[currentDay] && weeklySchedule[currentDay].timeSlots[currentTime]) {
          playCurrentTrack();
        }
      }, 60000); // Check every minute
    }

    return () => {
      if (absoluteTimerRef.current) {
        clearInterval(absoluteTimerRef.current);
      }
    };
  };

  const handleAuthCallback = async (code) => {
    try {
      const response = await axios.get(`${API}/auth/callback?code=${code}`);
      const { access_token, refresh_token } = response.data;

      setAccessToken(access_token);
      setRefreshToken(refresh_token);

      localStorage.setItem('spotify_access_token', access_token);
      localStorage.setItem('spotify_refresh_token', refresh_token);

      // Clean up URL
      window.history.replaceState({}, document.title, '/');
    } catch (error) {
      console.error('Auth callback error:', error);
      alert('Authentication failed. Please try again.');
    }
  };

  const handleLogin = async () => {
    try {
      const response = await axios.get(`${API}/auth/login`);
      window.location.href = response.data.auth_url;
    } catch (error) {
      console.error('Login error:', error);
      alert('Failed to start login process');
    }
  };

  const loadUserProfile = async () => {
    try {
      const response = await axios.get(`${API}/spotify/user?access_token=${accessToken}`);
      setUser(response.data);

      // Load saved timer state
      const savedState = localStorage.getItem(`timer_state_${response.data.id}`);
      if (savedState) {
        const state = JSON.parse(savedState);
        setTimeRemaining(state.timeRemaining || timerDuration * 60);
        setCurrentTrackIndex(state.currentTrackIndex || 0);
        setTrackPositions(state.trackPositions || {});
        setWeeklySchedule(state.weeklySchedule || {});
        setPlaybackTimingMode(state.playbackTimingMode || 'start');
        setAbsoluteTimeMode(state.absoluteTimeMode || false);
        setAbsoluteTimeSlots(state.absoluteTimeSlots || { hourly: false, halfHourly: false, custom: {} });
      }
    } catch (error) {
      console.error('Failed to load user profile:', error);
      // Token might be expired, try to refresh
      if (refreshToken) {
        await refreshAccessToken();
      }
    }
  };

  const refreshAccessToken = async () => {
    try {
      const response = await axios.post(`${API}/auth/refresh`, { refresh_token: refreshToken });
      const { access_token } = response.data;
      setAccessToken(access_token);
      localStorage.setItem('spotify_access_token', access_token);
    } catch (error) {
      console.error('Token refresh failed:', error);
      // Clear tokens and require re-login
      localStorage.removeItem('spotify_access_token');
      localStorage.removeItem('spotify_refresh_token');
      setAccessToken(null);
      setRefreshToken(null);
    }
  };

  const loadDevices = async () => {
    try {
      const response = await axios.get(`${API}/spotify/devices?access_token=${accessToken}`);
      setDevices(response.data.devices || []);
    } catch (error) {
      console.error('Failed to load devices:', error);
    }
  };

  const loadTimerSettings = async () => {
    if (!user?.id) return;

    try {
      const response = await axios.get(`${API}/timer/settings/${user.id}`);
      const settings = response.data;

      setTimerDuration(settings.timer_duration_minutes);
      setPlayDuration(settings.play_duration_seconds);
      setSelectedTracks(settings.selected_tracks);
    } catch (error) {
      console.error('Failed to load timer settings:', error);
    }
  };

  const saveTimerSettings = async () => {
    if (!user?.id) return;

    try {
      await axios.post(`${API}/timer/settings`, {
        user_id: user.id,
        timer_duration_minutes: timerDuration,
        play_duration_seconds: playDuration,
        selected_tracks: selectedTracks
      });
    } catch (error) {
      console.error('Failed to save timer settings:', error);
    }
  };

  const searchTracks = async () => {
    if (!searchQuery.trim()) return;

    setIsSearching(true);
    try {
      const response = await axios.get(`${API}/spotify/search?q=${encodeURIComponent(searchQuery)}&access_token=${accessToken}`);
      setSearchResults(response.data.tracks || []);
    } catch (error) {
      console.error('Search failed:', error);
      alert('Search failed. Please try again.');
    } finally {
      setIsSearching(false);
    }
  };

  const selectTrack = (track) => {
    if (selectedTracks.length >= 5) {
      alert('You can only select up to 5 tracks');
      return;
    }

    if (selectedTracks.find(t => t.id === track.id)) {
      alert('Track already selected');
      return;
    }

    const newTracks = [...selectedTracks, track];
    setSelectedTracks(newTracks);
    saveTimerSettings();
  };

  const removeTrack = (trackId) => {
    const newTracks = selectedTracks.filter(t => t.id !== trackId);
    setSelectedTracks(newTracks);
    saveTimerSettings();
  };

  const playCurrentTrack = async () => {
    if (selectedTracks.length === 0) {
      if (notificationsEnabled) {
        new Notification('Spotify Timer', {
          body: 'Please select at least one track',
          icon: '/favicon.ico'
        });
      }
      return;
    }

    const track = selectedTracks[currentTrackIndex];
    const savedPosition = trackPositions[track.uri] || 0;

    try {
      // First, try to get available devices
      console.log('Getting available devices...');
      const devicesResponse = await axios.get(`${API}/spotify/devices?access_token=${accessToken}`);
      const availableDevices = devicesResponse.data.devices || [];

      console.log('Available devices:', availableDevices);

      if (availableDevices.length === 0) {
        const message = 'No Spotify devices found. Please:\n1. Open Spotify on your computer/phone\n2. Play any song briefly to activate the device\n3. Try again';
        if (notificationsEnabled) {
          new Notification('Spotify Timer - Device Error', {
            body: message,
            icon: '/favicon.ico'
          });
        } else {
          alert(message);
        }
        return;
      }

      // Use the first available device
      const targetDevice = availableDevices[0];
      console.log('Using device:', targetDevice.name);

      // Make sure the device is active by transferring playback to it
      try {
        await axios.put(`${API}/spotify/transfer-playback?access_token=${accessToken}`, {
          device_ids: [targetDevice.id],
          play: false
        });

        // Wait a moment for transfer to complete
        await new Promise(resolve => setTimeout(resolve, 1000));
      } catch (transferError) {
        console.log('Playback transfer failed, continuing anyway:', transferError);
      }

      // Calculate position based on timing mode
      let startPosition = savedPosition;
      if (playbackTimingMode === 'end') {
        // If mode is 'end', calculate from end of play duration
        const endPosition = savedPosition + (playDuration * 1000);
        startPosition = Math.max(0, endPosition - (playDuration * 1000));
      }

      // Now try to play the track
      console.log(`Playing ${track.name} from position ${startPosition}ms`);
      await axios.post(`${API}/spotify/play?access_token=${accessToken}`, {
        track_uri: track.uri,
        position_ms: startPosition,
        device_id: targetDevice.id
      });

      setCurrentlyPlaying(track);
      setIsPlaying(true); // Set playing state

      if (notificationsEnabled) {
        new Notification('Spotify Timer', {
          body: `Now playing: ${track.name} for ${playDuration} seconds`,
          icon: '/favicon.ico'
        });
      }

      // Set up playback monitoring
      playbackIntervalRef.current = setInterval(async () => {
        try {
          const response = await axios.get(`${API}/spotify/playback?access_token=${accessToken}`);
          const state = response.data;

          if (state.is_playing && state.track) {
            const newPosition = state.progress_ms;
            const playedDuration = newPosition - startPosition;

            // Update track position
            setTrackPositions(prev => ({
              ...prev,
              [track.uri]: newPosition
            }));

            // Save position to backend
            await axios.post(`${API}/timer/track-position`, {
              user_id: user.id,
              track_uri: track.uri,
              current_position_ms: newPosition
            });

            // Stop after play duration
            if (playedDuration >= playDuration * 1000) {
              await axios.post(`${API}/spotify/pause?access_token=${accessToken}&device_id=${targetDevice.id}`);
              setCurrentlyPlaying(null);
              setIsPlaying(false); // Song finished playing

              // Move to next track for next cycle
              setCurrentTrackIndex(prev => (prev + 1) % selectedTracks.length);

              // NOW reset the timer for the next cycle (only if not in absolute mode)
              if (!absoluteTimeMode) {
                setTimeRemaining(timerDuration * 60);
              }

              clearInterval(playbackIntervalRef.current);

              if (notificationsEnabled) {
                new Notification('Spotify Timer', {
                  body: `Finished playing ${track.name}. Timer reset for next cycle.`,
                  icon: '/favicon.ico'
                });
              }
            }
          }
        } catch (error) {
          console.error('Playback monitoring error:', error);
        }
      }, 1000);

    } catch (error) {
      console.error('Playback failed:', error);

      let errorMessage = 'Playback failed. ';

      if (error.response) {
        // Server responded with error status
        const status = error.response.status;
        const detail = error.response.data?.detail || error.response.data?.message || 'Unknown error';

        if (status === 404) {
          errorMessage = 'No active Spotify device found. Please:\n1. Open Spotify on your computer/phone\n2. Play any song briefly to activate the device\n3. Try again';
        } else if (status === 403) {
          errorMessage = 'Playback failed. Make sure you have Spotify Premium - it\'s required for remote control.';
        } else {
          errorMessage = `Playback failed (${status}): ${detail}\n\nTry:\n1. Opening Spotify on another device\n2. Playing a song briefly\n3. Refreshing this page`;
        }
      } else if (error.request) {
        // Network error
        errorMessage = 'Network error. Please check your connection and try again.';
      } else {
        // Other error
        errorMessage = `Playback failed: ${error.message}\n\nTry:\n1. Opening Spotify on another device\n2. Playing a song briefly\n3. Refreshing this page`;
      }

      if (notificationsEnabled) {
        new Notification('Spotify Timer - Error', {
          body: errorMessage,
          icon: '/favicon.ico'
        });
      } else {
        alert(errorMessage);
      }
    }
  };

  const startTimer = () => {
    if (selectedTracks.length === 0) {
      alert('Please select at least one track');
      return;
    }
    setIsTimerRunning(true);
    saveTimerSettings();
  };

  const stopTimer = () => {
    setIsTimerRunning(false);
    if (playbackIntervalRef.current) {
      clearInterval(playbackIntervalRef.current);
    }
  };

  const fullStop = async () => {
    setIsTimerRunning(false);
    setIsPlaying(false);
    setTimeRemaining(timerDuration * 60);
    setCurrentTrackIndex(0);
    setTrackPositions({});
    setCurrentlyPlaying(null);

    if (playbackIntervalRef.current) {
      clearInterval(playbackIntervalRef.current);
    }

    try {
      await axios.post(`${API}/spotify/pause?access_token=${accessToken}`);
    } catch (error) {
      console.error('Failed to pause:', error);
    }
  };

  const applyCustomTimer = () => {
    const minutes = parseFloat(customTimerInput);
    if (minutes >= 0.1 && minutes <= 120) {
      setTimerDuration(minutes);
      setTimeRemaining(minutes * 60);
      setCustomTimerInput('');
      saveTimerSettings();
    } else {
      alert('Please enter a valid number between 0.1 and 120 minutes (0.1 = 6 seconds)');
    }
  };

  const toggleWeeklyScheduleSlot = (day, timeSlot) => {
    setWeeklySchedule(prev => ({
      ...prev,
      [day]: {
        ...prev[day],
        timeSlots: {
          ...prev[day].timeSlots,
          [timeSlot]: !prev[day].timeSlots[timeSlot]
        }
      }
    }));
  };

  const toggleWholeDay = (day) => {
    const allSelected = !weeklySchedule[day]?.wholeDay;
    setWeeklySchedule(prev => {
      const newTimeSlots = {};
      TIME_SLOTS.forEach(slot => {
        newTimeSlots[slot] = allSelected;
      });

      return {
        ...prev,
        [day]: {
          wholeDay: allSelected,
          timeSlots: newTimeSlots
        }
      };
    });
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  const formatTrackDuration = (ms) => {
    const seconds = Math.floor(ms / 1000);
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  // If not authenticated, show login
  if (!accessToken) {
    return (
      <div className="spotify-timer">
        <div className="login-container">
          <div className="logo">
            <h1> Spotify Timer Pro</h1>
            <p>Advanced timer with scheduling and mobile support</p>
          </div>
          <button onClick={handleLogin} className="spotify-login-btn">
            Login with Spotify
          </button>
        </div>
      </div>
    );
  }

  // If no premium, show upgrade message
  if (user && !user.is_premium) {
    return (
      <div className="spotify-timer">
        <div className="premium-required">
          <h2>Spotify Premium Required</h2>
          <p>This app requires Spotify Premium to control playback.</p>
          <a href="https://www.spotify.com/premium/" target="_blank" rel="noopener noreferrer" className="upgrade-btn">
            Upgrade to Premium
          </a>
          <button onClick={() => {
            localStorage.clear();
            window.location.reload();
          }} className="logout-btn">
            Logout
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="spotify-timer">
      {/* Header */}
      <div className="header">
        <h1> Spotify Timer Pro</h1>
        <div className="user-info">
          <span>Welcome, {user?.display_name}</span>
          <button 
            className="settings-btn"
            onClick={() => setShowSettings(!showSettings)}
          >

          </button>
        </div>
      </div>

      {/* Navigation Tabs */}
      <div className="tab-navigation">
        <button 
          className={activeTab === 'timer' ? 'tab-btn active' : 'tab-btn'}
          onClick={() => setActiveTab('timer')}
        >
          Timer
        </button>
        <button 
          className={activeTab === 'schedule' ? 'tab-btn active' : 'tab-btn'}
          onClick={() => setActiveTab('schedule')}
        >
          Schedule
        </button>
        <button 
          className={activeTab === 'tracks' ? 'tab-btn active' : 'tab-btn'}
          onClick={() => setActiveTab('tracks')}
        >
          Tracks
        </button>
      </div>

      {/* Timer Tab */}
      {activeTab === 'timer' && (
        <div className="timer-section">
          {/* Timer Display */}
          <div className="timer-display">
            <div className="time-remaining">
              {timeRemaining === 0 && isPlaying ? 'PLAYING...' : formatTime(timeRemaining)}
            </div>
            <div className="timer-info">
              Timer: {timerDuration >= 1 ? `${timerDuration} min` : `${Math.round(timerDuration * 60)}s`} | Play: {playDuration}s | Track: {currentTrackIndex + 1}/{selectedTracks.length}
            </div>
            <div className="timer-mode">
              Mode: {absoluteTimeMode ? 'Absolute Time' : 'Relative Timer'} | 
              Timing: {playbackTimingMode === 'start' ? 'From Start' : 'From End'}
            </div>
          </div>

          {/* Current Track Info */}
          {selectedTracks.length > 0 && (
            <div className="current-track">
              <h3>Current Track:</h3>
              <div className="track-item">
                {selectedTracks[currentTrackIndex]?.album?.images?.[0] && (
                  <img 
                    src={selectedTracks[currentTrackIndex].album.images[0].url} 
                    alt={selectedTracks[currentTrackIndex].name}
                    className="track-image"
                  />
                )}
                <div className="track-details">
                  <div className="track-name">{selectedTracks[currentTrackIndex]?.name}</div>
                  <div className="track-artist">
                    {selectedTracks[currentTrackIndex]?.artists?.map(a => a.name).join(', ')}
                  </div>
                  <div className="track-position">
                    Position: {formatTrackDuration(trackPositions[selectedTracks[currentTrackIndex]?.uri] || 0)} / 
                    {formatTrackDuration(selectedTracks[currentTrackIndex]?.duration_ms || 0)}
                  </div>
                </div>
              </div>
              {currentlyPlaying && (
                <div className="now-playing">
                   Now Playing: {playDuration}s segment
                </div>
              )}
            </div>
          )}

          {/* Controls */}
          <div className="controls">
            <button 
              onClick={startTimer} 
              disabled={isTimerRunning || selectedTracks.length === 0}
              className="control-btn start-btn"
            >
              START
            </button>
            <button 
              onClick={stopTimer} 
              disabled={!isTimerRunning}
              className="control-btn stop-btn"
            >
              STOP
            </button>
            <button 
              onClick={fullStop}
              className="control-btn full-stop-btn"
            >
              FULL STOP
            </button>
          </div>

          {/* Advanced Settings Panel */}
          {showSettings && (
            <div className="settings-panel">
              <h3>Advanced Settings</h3>

              {/* Timer Mode Toggle */}
              <div className="setting-group">
                <label>Timer Mode</label>
                <div className="toggle-group">
                  <button
                    onClick={() => setAbsoluteTimeMode(false)}
                    className={!absoluteTimeMode ? 'toggle-btn active' : 'toggle-btn'}
                  >
                    Relative Timer
                  </button>
                  <button
                    onClick={() => setAbsoluteTimeMode(true)}
                    className={absoluteTimeMode ? 'toggle-btn active' : 'toggle-btn'}
                  >
                    Absolute Time
                  </button>
                </div>
              </div>

              {/* Playback Timing Mode */}
              <div className="setting-group">
                <label>Cycle Calculation</label>
                <div className="toggle-group">
                  <button
                    onClick={() => setPlaybackTimingMode('start')}
                    className={playbackTimingMode === 'start' ? 'toggle-btn active' : 'toggle-btn'}
                  >
                    From Start
                  </button>
                  <button
                    onClick={() => setPlaybackTimingMode('end')}
                    className={playbackTimingMode === 'end' ? 'toggle-btn active' : 'toggle-btn'}
                  >
                    From End
                  </button>
                </div>
                <small>From Start: Timer starts when song begins | From End: Timer starts when song ends</small>
              </div>

              {/* Absolute Time Settings */}
              {absoluteTimeMode && (
                <div className="setting-group">
                  <label>Absolute Time Slots</label>
                  <div className="checkbox-group">
                    <label className="checkbox-label">
                      <input
                        type="checkbox"
                        checked={absoluteTimeSlots.hourly}
                        onChange={(e) => setAbsoluteTimeSlots(prev => ({
                          ...prev,
                          hourly: e.target.checked
                        }))}
                      />
                      Every Hour (00:00)
                    </label>
                    <label className="checkbox-label">
                      <input
                        type="checkbox"
                        checked={absoluteTimeSlots.halfHourly}
                        onChange={(e) => setAbsoluteTimeSlots(prev => ({
                          ...prev,
                          halfHourly: e.target.checked
                        }))}
                      />
                      Every Half Hour (30:00)
                    </label>
                  </div>
                </div>
              )}

              {/* Notifications */}
              <div className="setting-group">
                <label className="checkbox-label">
                  <input
                    type="checkbox"
                    checked={notificationsEnabled}
                    onChange={(e) => setNotificationsEnabled(e.target.checked)}
                  />
                  Enable Notifications
                </label>
              </div>

              {/* Timer Duration (only for relative mode) */}
              {!absoluteTimeMode && (
                <div className="setting-group">
                  <label>Timer Duration</label>
                  <div className="timer-presets">
                    {TIMER_PRESETS.map(preset => (
                      <button
                        key={preset.label}
                        onClick={() => {
                          setTimerDuration(preset.value);
                          setTimeRemaining(preset.value * 60);
                          saveTimerSettings();
                        }}
                        className={Math.abs(timerDuration - preset.value) < 0.1 ? 'preset-btn active' : 'preset-btn'}
                      >
                        {preset.label}
                      </button>
                    ))}
                  </div>
                  <div className="custom-timer">
                    <input
                      type="number"
                      value={customTimerInput}
                      onChange={(e) => setCustomTimerInput(e.target.value)}
                      placeholder="Custom minutes (0.1-120)"
                      min="0.1"
                      max="120"
                      step="0.1"
                    />
                    <button onClick={applyCustomTimer} className="apply-btn">Apply</button>
                  </div>
                </div>
              )}

              {/* Play Duration */}
              <div className="setting-group">
                <label>Play Duration (seconds)</label>
                <input
                  type="range"
                  min="10"
                  max="60"
                  value={playDuration}
                  onChange={(e) => {
                    setPlayDuration(parseInt(e.target.value));
                    saveTimerSettings();
                  }}
                />
                <span>{playDuration}s</span>
              </div>
            </div>
          )}
        </div>
      )}

      {/* Schedule Tab */}
      {activeTab === 'schedule' && (
        <div className="schedule-section">
          <h3>Weekly Schedule</h3>
          <p>Select when the timer should be active (7:00 AM - 5:00 PM, Monday-Friday)</p>

          <div className="weekly-schedule">
            <div className="schedule-header">
              <div className="time-label">Time</div>
              {DAYS.map(day => (
                <div key={day} className="day-header">
                  <div className="day-name">{day.substring(0, 3)}</div>
                  <label className="whole-day-checkbox">
                    <input
                      type="checkbox"
                      checked={weeklySchedule[day]?.wholeDay || false}
                      onChange={() => toggleWholeDay(day)}
                    />
                    Whole Day
                  </label>
                </div>
              ))}
            </div>

            <div className="schedule-grid">
              {TIME_SLOTS.map(timeSlot => (
                <div key={timeSlot} className="schedule-row">
                  <div className="time-slot-label">{timeSlot}</div>
                  {DAYS.map(day => (
                    <div key={`${day}-${timeSlot}`} className="schedule-cell">
                      <input
                        type="checkbox"
                        checked={weeklySchedule[day]?.timeSlots?.[timeSlot] || false}
                        onChange={() => toggleWeeklyScheduleSlot(day, timeSlot)}
                        className="schedule-checkbox"
                      />
                    </div>
                  ))}
                </div>
              ))}
            </div>
          </div>
        </div>
      )}

      {/* Tracks Tab */}
      {activeTab === 'tracks' && (
        <div className="track-selection">
          <h3>Selected Tracks ({selectedTracks.length}/5)</h3>

          {/* Search */}
          <div className="search-section">
            <div className="search-input">
              <input
                type="text"
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                placeholder="Search for tracks..."
                onKeyPress={(e) => e.key === 'Enter' && searchTracks()}
              />
              <button onClick={searchTracks} disabled={isSearching}>
                {isSearching ? 'Searching...' : 'Search'}
              </button>
            </div>
          </div>

          {/* Selected Tracks */}
          {selectedTracks.length > 0 && (
            <div className="selected-tracks">
              <h4>Your Tracks:</h4>
              {selectedTracks.map((track, index) => (
                <div key={track.id} className="track-item selected">
                  <span className="track-number">{index + 1}.</span>
                  {track.album?.images?.[2] && (
                    <img src={track.album.images[2].url} alt={track.name} className="track-image-small" />
                  )}
                  <div className="track-info">
                    <div className="track-name">{track.name}</div>
                    <div className="track-artist">{track.artists?.map(a => a.name).join(', ')}</div>
                  </div>
                  <button onClick={() => removeTrack(track.id)} className="remove-btn"></button>
                </div>
              ))}
            </div>
          )}

          {/* Search Results */}
          {searchResults.length > 0 && (
            <div className="search-results">
              <h4>Search Results:</h4>
              {searchResults.map((track) => (
                <div key={track.id} className="track-item">
                  {track.album?.images?.[2] && (
                    <img src={track.album.images[2].url} alt={track.name} className="track-image-small" />
                  )}
                  <div className="track-info">
                    <div className="track-name">{track.name}</div>
                    <div className="track-artist">{track.artists?.map(a => a.name).join(', ')}</div>
                    <div className="track-duration">{formatTrackDuration(track.duration_ms)}</div>
                  </div>
                  <button 
                    onClick={() => selectTrack(track)} 
                    disabled={selectedTracks.length >= 5 || selectedTracks.find(t => t.id === track.id)}
                    className="select-btn"
                  >
                    {selectedTracks.find(t => t.id === track.id) ? 'Selected' : 'Select'}
                  </button>
                </div>
              ))}
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default SpotifyTimer;
